import React from "react";
// import profile_pic from '../images/profile_pic.png';




const Card = ({id, name, url, skills}) => {
    return(
        <div className="ba Card_container flex flex-column items-center ">
            {/* <img className="ba Card_picture" id={id} alt={id} 
                 title={name} src={profile_pic}></img>  */}
            <div>
                <div className={`ba Card_picture ${id}`}></div>
            </div>
            <a title={url} href={url}><h1 className="ba avenir"> {name}</h1></a>
            { skills && <h3>{skills.map(skill => skill)}</h3> }
        </div>
    )
}


export default Card;
