import React from 'react'


const skillsBar = [
    {
      name: "HTML555",
      // To add a custom svg instead of font-awesome icons, add svg path below otherwise just comment it out
      svg: "M0 32l34.9 395.8L191.5 480l157.6-52.2L384 32H0zm308.2 127.9H124.4l4.1 49.4h175.6l-13.6 148.4-97.9 27v.3h-1.1l-98.7-27.3-6-75.8h47.7L138 320l53.5 14.5 53.7-14.5 6-62.2H84.3L71.5 112.2h241.1l-4.4 47.7z",
      faClass: "fab fa-html5",
    },
    {
      name: "CSS3",
      // svg: '',
      faClass: "fab fa-css3",
    },
    {
      name: "Javascript",
      // svg: '',
      faClass: "fab fa-js",
    },
    {
      name: "SASS",
      // svg: '',
      faClass: "fab fa-sass",
    },
    {
      name: "Node",
      // svg: '',
      faClass: "fab fa-node",
    },
    {
      name: "Python",
      // svg: '',
      faClass: "fab fa-python",
    },
    {
      name: "Java",
      // svg: '',
      faClass: "fab fa-java",
    },
    {
      name: "PHP",
      // svg: '',
      faClass: "fab fa-php",
    },
    {
      name: "Database",
      // svg: '',
      faClass: "fas fa-database",
    },
    {
      name: "AWS",
      // svg: '',
      faClass: "fab fa-aws",
    },
  ];
const Skills = ({ label, svg, faClass }) => {
    return (
        <div  className="bg-white" style={{display: 'inline', textAlign: 'center'}}>
            {svg ?  <svg  aria-hidden="true" focusable="false" data-icon={label} role="i" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 384 512"><path fill="currentcolor" d={svg} /></svg> : <i className={`${faClass} mx-auto my-auto`}></i>}
            <h6 className="mt-2">{label}</h6>
        </div>
    )
}

export default Skills
export  {skillsBar}
