import React from 'react';
import profile_pic from '../images/profile_pic.png';
import 'animate.css';
import { useInView } from "react-intersection-observer";




const About = () => {
    const [ref, inView] = useInView({ threshold: 0.5 })
    return (
        // <div className='About_container flex flex-column items-center avenir '>
              <div className={inView ? "animate__animated animate__fadeInUp animate__fast About_container flex flex-column items-center avenir" : "anim2 About_container flex flex-column items-center avenir"} > 

                <div className='About_box  flex items-center  '>
                    <img className="About_pic  justify-start contain w-50" id="profile_pic" alt="Profile Pic" 
                        title="Profile Picture" src={profile_pic}></img>   

                    <div ref={ref} className= " About_text  flex flex-column items-center" >
                        <h1 className=' f1 measure bb bw2 b--dark-blue  '> About Me </h1>
                        <h3 className=' pa4 lh-copy i tj fw4 '> Software Engineer who loves to transform ideas into reality using code.
                                            With over three years of developing web applications using the latest front-end and UI/UX 
                                            technologies and full-fledged APIs. Motivated designer and developer with experience 
                                            creating custom websites with ReactJs, Node, Express, Mongo, and PostgreSQL. </h3>
                    </div> 

                </div>

                <div className='About_logos pt4 flex flex-wrap ma4   '>
                    <i class="pa3 devicon-html5-plain-wordmark colored "></i>
                    <i class="pa3 devicon-react-original-wordmark colored"></i>
                    <i class="pa3 devicon-css3-plain-wordmark colored"></i>
                    <i class="pa3 devicon-sass-original colored"></i>
                    <i class="pa3 devicon-nodejs-plain-wordmark colored"></i>
                    <i class="pa3 devicon-javascript-plain colored"></i>
                    <i class="pa3 devicon-php-plain colored"></i>
                    <i class="pa3 devicon-mongodb-plain-wordmark colored"></i>
                    <i class="pa3 devicon-cplusplus-plain-wordmark colored"></i>

                </div>    

        </div>
    )
}



export default About;
